sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var question = current.question.getRefRecord();
	var url = gs.getProperty('glide.servlet.uri') + 'x_nuvo_ko_portal.do#!/questions/' + question.getValue('sys_id');

	var gr = new GlideRecord('x_nuvo_ko_notifications');
	gr.initialize();
	gr.user = question.getElement('asker');
	gr.question = question.getValue('sys_id');
	gr.title = "Your Question Has a New Answer";
	gr.html = "<p>Your question <strong><a href= '" + url + "'>" + question.getValue('question') + "</a></strong> has a new answer.</p><p><p>Poster: "+current.getElement('poster').getRefRecord().getValue('name') +"</p><p>Answer: "+current.getValue('answer')+"</p>";
	gr.insert();


	var stars = question.getValue('stars');
	if(stars != null){
		stars = stars.split(',');
		for(var i = 0; i<stars.length; i++){
			if(stars[i]!= current.getElement('poster').getRefRecord().getValue('sys_id')){
				gr.initialize();
				gr.user = stars[i];
				gr.question = question.getValue('sys_id');
				gr.title = "Starred Question Has New Answer";
				gr.html = "<p>A question you have starred, <strong><a href= '" + url + "'>" + question.getValue('question') + "</a></strong>, has a new answer.</p><p><p>Poster: "+current.getElement('poster').getRefRecord().getValue('name') +"</p><p>Answer: "+current.getValue('answer')+"</p>";
				gr.insert();
			}
		}
	} 


})(current, previous);
